mapper.insert:
insert into project (projectId, prjectName, createTime
  )
values (#{projectId,jdbcType=INTEGER}, #{prjectName,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}
  )

mapper.update:
update project
set prjectName = #{prjectName,jdbcType=VARCHAR},
  createTime = #{createTime,jdbcType=TIMESTAMP}
where projectId = #{projectId,jdbcType=INTEGER}

mapper.select:
select projectId, prjectName, createTime
from project
where projectId = #{projectId,jdbcType=INTEGER}
