mapper.delete:
delete from user
where userId = #{userId,jdbcType=INTEGER}
  and userName = #{userName,jdbcType=VARCHAR}

mapper.update:
update user
set priority = #{priority,jdbcType=INTEGER},
  password = #{password,jdbcType=VARCHAR}
where userId = #{userId,jdbcType=INTEGER}
  and userName = #{userName,jdbcType=VARCHAR}

mapper.select:
select userId, userName, priority, password
from user
where userId = #{userId,jdbcType=INTEGER}
  and userName = #{userName,jdbcType=VARCHAR}
